import React, {Component} from "react";
import Header from "../component/header";
import BeritaTerkini from "../component/berita_terkini";
import Berita from "../component/berita"
import "../style/css-final.css";
import "../style/bootstrap.min.css";

// const apiKey = "02d1c2c457c14f739ed709c4bca05f3c"
// const baseUrl = "https://newsapi.org/v2/"
// const urlHeadline = baseUrl+"top-headlines?county=id&apiKey="+apiKey;

class News extends Component{

    state = {
        listNews : [],
        isloading : true,
        country : 'sport'
    }
    componentDidMount = () => {
        const self = this;
        axios
            .get(`${baseUrl}top-headlines?county=id&country=${this.sprot}&apiKey=${apiKey}`)
            .then(function(response){
                self.setState({ listNews: response.data.article, isloading: false});
            })
            .catch(function(error){
                self.setState({ isloading:false})
            })
    }
    render(){
        const { listNews, isloading} = this.state;
        const topHeadlines = listNews.filter(item => {
            if (item.content !== null && item.image !== null){
                return item;
            }
            return false;
        });

        const headlineNews = topHeadlines.map((item) => {
            return(
                <div>
                    <Berita/>
                    author={item.author}
                    img={item.urlToImage}
                    title={item.title}
                    description={item.description}
                    publish={item.publishedAt}
                    url={item.url}
                </div>
            );
        });

        return (
            <div>
                <Header/>
                <BeritaTerkini/>
                {isloading ? <div style={{ textAlign:"center"}}>looding...</div> : headlineNews}
            </div>
        );
    }
}

export default News;
