import React, { Component } from "react";
import axios from "axios";
// import { withRouter } from "react-router-dom";
import Header from "./header";

class SignIn extends Component {
  state = { namaPengguna: "", kataKunci: "" };

  changeInput = e => {
    console.warn("cek event target", e.target.value);

    this.setState({ [e.target.name]: e.target.value });
  };

  postLogin = () => {
    const { namaPengguna, kataKunci } = this.state;
    const data = {
      username: namaPengguna,
      password: kataKunci
    };
    const self = this;
    axios
      .post("https://gading.free.beeceptor.com/login", data)
      .then(function (response) {
        console.warn("cek respon data login ", response.data.first_name);
        if (response.data.first_name){
        localStorage.setItem("is_login", true);
        localStorage.setItem("first_name", response.data.first_name);
        localStorage.setItem("last_name", response.data.last_name);
        localStorage.setItem("full_name", response.data.full_name);
        localStorage.setItem("email", response.data.email);
        self.props.history.push("/profile");
        }
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  render() {
    console.log("state", this.state);
    return (
      <React.Fragment>
        <Header {...this.props} />
        <section className="content signin">
          <form onSubmit={e => e.preventDefault()}>
            <h4>SignIn</h4>
            <div class="wrapper fadeInDown">
                <div id="formContent">
                    <div class="fadeIn first">
                        <img src="http://danielzawadzki.com/codepen/01/icon.svg" id="icon" alt="User Icon" />
                    </div>
                    <form>
                        <input type="text" id="login" class="fadeIn second" name="login" placeholder="login"/>
                        <input type="text" id="password" class="fadeIn third" name="login" placeholder="password"/>
                        <input type="submit" class="fadeIn fourth" value="Log In"/>
                    </form>
                    <div id="formFooter">
                        <a class="underlineHover" href="#">Forgot Password?</a>
                    </div>
                </div>
            </div>
          </form>
        </section>
      </React.Fragment>
    );
  }
}

export default SignIn;
