import React, { Component } from "react";
import axios from "axios";
// import { withRouter } from "react-router-dom";
import Header from "./header";

class SignIn extends Component {
  state = { namaPengguna: "", kataKunci: "" };

  changeInput = e => {
    console.warn("cek event target", e.target.value);

    this.setState({ [e.target.name]: e.target.value });
  };

  postLogin = () => {
    const { namaPengguna, kataKunci } = this.state;
    const data = {
      username: namaPengguna,
      password: kataKunci
    };
    const self = this;
    axios
      .post("https://gading.free.beeceptor.com/login", data)
      .then(function (response) {
        console.warn("cek respon data login ", response.data.first_name);
        if (response.data.first_name){
        localStorage.setItem("is_login", true);
        localStorage.setItem("first_name", response.data.first_name);
        localStorage.setItem("last_name", response.data.last_name);
        localStorage.setItem("full_name", response.data.full_name);
        localStorage.setItem("email", response.data.email);
        self.props.history.push("/profile");
        }
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  render() {
    console.log("state", this.state);
    return (
      <React.Fragment>
        <Header {...this.props} />
        <section className="content signin">
          <form onSubmit={e => e.preventDefault()}>
            <h4>SignIn</h4>
            <div>
              <input
                type="text"
                name="namaPengguna"
                placeholder="Username"
                onChange={e => this.changeInput(e)}
              />
            </div>
            <div>
              <input
                type="password"
                name="kataKunci"
                placeholder="Password"
                onChange={e => this.changeInput(e)}
              />
            </div>
            <button
              style={{
                height: "30px",
                width: "50px",
                backgroundColor: "red",
                color: "white"
              }}
              onClick={() => this.postLogin()}
            >
              SignIn
            </button>
            <button
              style={{
                height: "30px",
                width: "50px",
                backgroundColor: "red",
                color: "white"
              }}
              type="reset"
            >
              Reset
            </button>
          </form>
        </section>
      </React.Fragment>
    );
  }
}

export default SignIn;
